import "jsr:@supabase/functions-js/edge-runtime.d.ts";

interface EventData {
  id: string;
  date: string;
  time: string;
  customerName: string;
  customerPhone: string;
  childName: string;
  packageType: 'Abra' | 'Kadabra' | 'Abrakadabra';
  totalAmount: number;
  deposit: number;
  remainingAmount: number;
  isPaid: boolean;
  notes: string;
  createdAt: string;
}

// Google Sheets configuration
const SPREADSHEET_ID = '13nNp7c8gSn0L3lCWHbJmHcCUZt9iUY7XUxP7SJLCh6s';
const RANGE = 'Sheet1!A:I';

// Service account configuration (you'll need to set these as environment variables)
const SERVICE_ACCOUNT_EMAIL = Deno.env.get('GOOGLE_SERVICE_ACCOUNT_EMAIL');
const PRIVATE_KEY = Deno.env.get('GOOGLE_PRIVATE_KEY')?.replace(/\\n/g, '\n');

// Convert Event to Google Sheets row format
const eventToSheetRow = (event: EventData): string[] => {
  return [
    event.date,                                    // Fecha
    `${event.customerName} (${event.childName})`, // Nombre
    event.customerPhone,                           // Teléfono
    event.packageType,                            // Paquete
    event.isPaid ? 'Pagado' : 'Pendiente',       // Estado
    event.deposit.toString(),                      // AnticipoPagado
    event.totalAmount.toString(),                  // TotalEvento
    event.isPaid ? event.date : '',               // FechaPago
    'No'                                          // NotificadoLunes
  ];
};

// Generate JWT for Google API authentication
async function generateJWT(): Promise<string> {
  if (!SERVICE_ACCOUNT_EMAIL || !PRIVATE_KEY) {
    throw new Error('Service account credentials not configured');
  }

  const header = {
    alg: 'RS256',
    typ: 'JWT'
  };

  const now = Math.floor(Date.now() / 1000);
  const payload = {
    iss: SERVICE_ACCOUNT_EMAIL,
    scope: 'https://www.googleapis.com/auth/spreadsheets',
    aud: 'https://oauth2.googleapis.com/token',
    exp: now + 3600,
    iat: now
  };

  const encoder = new TextEncoder();
  const headerB64 = btoa(JSON.stringify(header)).replace(/=/g, '').replace(/\+/g, '-').replace(/\//g, '_');
  const payloadB64 = btoa(JSON.stringify(payload)).replace(/=/g, '').replace(/\+/g, '-').replace(/\//g, '_');

  const data = `${headerB64}.${payloadB64}`;

  // Import the private key
  const privateKeyObj = await crypto.subtle.importKey(
    'pkcs8',
    encoder.encode(PRIVATE_KEY),
    {
      name: 'RSASSA-PKCS1-v1_5',
      hash: 'SHA-256'
    },
    false,
    ['sign']
  );

  // Sign the JWT
  const signature = await crypto.subtle.sign(
    'RSASSA-PKCS1-v1_5',
    privateKeyObj,
    encoder.encode(data)
  );

  const signatureB64 = btoa(String.fromCharCode(...new Uint8Array(signature)))
    .replace(/=/g, '').replace(/\+/g, '-').replace(/\//g, '_');

  return `${data}.${signatureB64}`;
}

// Get access token from Google
async function getAccessToken(): Promise<string> {
  const jwt = await generateJWT();

  const response = await fetch('https://oauth2.googleapis.com/token', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/x-www-form-urlencoded'
    },
    body: new URLSearchParams({
      grant_type: 'urn:ietf:params:oauth:grant-type:jwt-bearer',
      assertion: jwt
    })
  });

  if (!response.ok) {
    const error = await response.text();
    throw new Error(`Failed to get access token: ${error}`);
  }

  const data = await response.json();
  return data.access_token;
}

// Save event to Google Sheets
async function saveToGoogleSheets(event: EventData): Promise<boolean> {
  try {
    const accessToken = await getAccessToken();
    const sheetRow = eventToSheetRow(event);

    const url = `https://sheets.googleapis.com/v4/spreadsheets/${SPREADSHEET_ID}/values/${RANGE}:append?valueInputOption=RAW&insertDataOption=INSERT_ROWS`;

    const response = await fetch(url, {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${accessToken}`,
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        values: [sheetRow]
      })
    });

    if (!response.ok) {
      const error = await response.text();
      console.error('Google Sheets API error:', error);
      return false;
    }

    console.log('Successfully saved to Google Sheets');
    return true;
  } catch (error) {
    console.error('Error saving to Google Sheets:', error);
    return false;
  }
}

// Load events from Google Sheets
async function loadFromGoogleSheets(): Promise<EventData[]> {
  try {
    const accessToken = await getAccessToken();

    const url = `https://sheets.googleapis.com/v4/spreadsheets/${SPREADSHEET_ID}/values/${RANGE}`;

    const response = await fetch(url, {
      headers: {
        'Authorization': `Bearer ${accessToken}`
      }
    });

    if (!response.ok) {
      const error = await response.text();
      console.error('Google Sheets API error:', error);
      return [];
    }

    const data = await response.json();
    const rows = data.values || [];

    // Skip header row and convert to events
    const events: EventData[] = [];
    for (let i = 1; i < rows.length; i++) {
      const row = rows[i];
      if (row && row.length >= 4) {
        const nombre = row[1] || '';
        const nameMatch = nombre.match(/^(.+?)\s*\((.+?)\)$/);
        const customerName = nameMatch ? nameMatch[1].trim() : nombre;
        const childName = nameMatch ? nameMatch[2].trim() : '';

        const event: EventData = {
          id: `sheet_${i}_${(row[0] || '').replace(/-/g, '')}`,
          date: row[0] || '',
          time: '15:00',
          customerName,
          customerPhone: row[2] || '',
          childName,
          packageType: (row[3] || 'Abra') as 'Abra' | 'Kadabra' | 'Abrakadabra',
          totalAmount: parseFloat(row[6] || '0'),
          deposit: parseFloat(row[5] || '0'),
          remainingAmount: parseFloat(row[6] || '0') - parseFloat(row[5] || '0'),
          isPaid: (row[4] || '').toLowerCase() === 'pagado',
          notes: '',
          createdAt: new Date().toISOString()
        };
        events.push(event);
      }
    }

    return events;
  } catch (error) {
    console.error('Error loading from Google Sheets:', error);
    return [];
  }
}

Deno.serve(async (req: Request) => {
  // Handle CORS
  if (req.method === 'OPTIONS') {
    return new Response(null, {
      status: 200,
      headers: {
        'Access-Control-Allow-Origin': '*',
        'Access-Control-Allow-Methods': 'GET, POST, PUT, DELETE, OPTIONS',
        'Access-Control-Allow-Headers': 'Content-Type, Authorization'
      }
    });
  }

  try {
    const url = new URL(req.url);
    const action = url.searchParams.get('action');

    if (req.method === 'POST' && action === 'save') {
      const event: EventData = await req.json();
      const success = await saveToGoogleSheets(event);

      return new Response(JSON.stringify({ success }), {
        headers: {
          'Content-Type': 'application/json',
          'Access-Control-Allow-Origin': '*'
        }
      });
    }

    if (req.method === 'GET' && action === 'load') {
      const events = await loadFromGoogleSheets();

      return new Response(JSON.stringify({ events }), {
        headers: {
          'Content-Type': 'application/json',
          'Access-Control-Allow-Origin': '*'
        }
      });
    }

    return new Response(JSON.stringify({ error: 'Invalid action' }), {
      status: 400,
      headers: {
        'Content-Type': 'application/json',
        'Access-Control-Allow-Origin': '*'
      }
    });
  } catch (error) {
    console.error('Edge function error:', error);
    return new Response(JSON.stringify({ error: error.message }), {
      status: 500,
      headers: {
        'Content-Type': 'application/json',
        'Access-Control-Allow-Origin': '*'
      }
    });
  }
});

